        /// <summary>
        /// 上传文件
        /// </summary>
        [HiddenApi]
        [HttpPost]
        public ReportResult<string> UploadFiles()
        {
            ReportResult<string> result = new ReportResult<string>();
            HttpRequest request = HttpContext.Current.Request;
            HttpFileCollection fileList = HttpContext.Current.Request.Files;

            string reportId = request["reportId"];
            string contractId = request["contractId"];
            string status = request["status"];
            string message = request["message"];

            if (fileList.Count > 0)
            {
                for (int i = 0; i < fileList.Count; i++)
                {
                    try
                    {
                        HttpPostedFile file = fileList[i];
                        string filename = file.FileName;
                        if (filename.Equals(""))
                        {
                            EmcConfig.ErrorLog.Error("上传失败:上传的文件信息不存在！");
                            result = SetReportResult<string>("下载失败:上传的文件信息不存在！", false, "");
                        }
                        string filePath = EmcConfig.CurrentRoot + "Files\\Upload\\";
                        string forceName = "upload";
                        string extendName = FileUtil.FilterExtendName(filename);
                        string templateFileName = forceName + DateTime.Now.ToString("yyyyMMddHHmmssfff") + extendName;

                        DirectoryInfo di = new DirectoryInfo(filePath);
                        if (!di.Exists) { di.Create(); }

                        file.SaveAs(filePath + templateFileName);
                        EmcConfig.InfoLog.Info(result);
                        result = SetReportResult<string>($"上传成功:{filename}", true, templateFileName);
                    }
                    catch (Exception ex)
                    {
                        EmcConfig.ErrorLog.Error(ex.Message, ex);
                        result = SetReportResult<string>($"上传文件写入失败：{ex.Message}", false, "");
                    }
                }
            }
            else
            {
                EmcConfig.ErrorLog.Error("上传失败:上传的文件信息不存在！");
                result = SetReportResult<string>("下载失败:上传的文件信息不存在！", false, "");
            }

            return result;
        }

        /// <summary>
        /// 返回结果参数
        /// </summary>
        private ReportResult<T> SetReportResult<T>(string message, bool submitResult, T content)
        {
            Type type = content.GetType();
            ReportResult<T> reportResult = new ReportResult<T>();
            reportResult.Message = message;
            reportResult.SumbitResult = submitResult;
            reportResult.Content = content;
            return reportResult;
        }

        /// <summary>
        /// 测试报告,带参数
        /// </summary>
        /// <param name="para"></param>
        /// <returns></returns>
        [HttpPost]
        [HiddenApi]
        [CompressContentAttribute]
        public IHttpActionResult CreateReportTest1(ReportParams para)
        {
            Task<ReportResult<string>> task = new Task<ReportResult<string>>(() => CreateReportTestAsync(para));
            task.Start();
            ReportResult<string> result = task.Result;
            return Json(result);
        }

        private ReportResult<string> CreateReportTestAsync(ReportParams para)
        {
            ReportResult<string> result = new ReportResult<string>();
            try
            {
                EmcConfig.SemLim.Wait();
                Stopwatch sw = new Stopwatch();
                sw.Start();

                ReportInfo reportInfo = new ReportInfo(new ReportParams
                {
                    JsonStr = para.JsonStr.Equals("") ? jsonStr1 : para.JsonStr,
                    ReportId = "QT2019-3015"
                });
                //生成报告
                string content = _report.ReportJsonToWord(reportInfo);
                sw.Stop();
                double time1 = (double)sw.ElapsedMilliseconds / 1000;
                result.Message = string.Format("报告生成成功,用时:" + time1);
                result.SumbitResult = true;
                result.Content = content;
                EmcConfig.InfoLog.Info("报告:" + result.Content + ",信息:" + result.Message);

            }
            catch (Exception ex)
            {
                EmcConfig.ErrorLog.Error(ex.Message, ex);
                throw;
            }
            finally
            {
                EmcConfig.SemLim.Release();
            }
            return result;
        }

