        private SemaphoreSlim semLim = new SemaphoreSlim(2);

        private async Task<string> ReportTestAsync()
        {
            var task = TestTask();
            Task.WaitAll(task);
            string result = await task;
            return result;
        }

        private Task<string> TestTask()
        {
            return Task.Run(() =>
            {
                semLim.Wait();
                StandardReportInfo standardReportInfo = new StandardReportInfo(new StandardReportParams
                {
                    JsonObject = (JObject)JsonConvert.DeserializeObject(jsonStr)
                });
                StandardReportResult result = _reportStandard.JsonToWordStandard(standardReportInfo);
                semLim.Release();
                return result.FileName;
            });
        }


